import java.util.ArrayList;
import java.util.List;

/**
 * 计数二进制子串
 *
 * @Author RUANWENJUN
 * @Creat 2018-05-03 18:48
 */

public class Problem696 {
    /**
     * 计算具有相同数目的0，1的子串，并且0 1 需要组合在一起，即不能出现三个字母左右不相等的情况
     *
     * @param s
     * @return
     */
    public int countBinarySubstrings(String s) {
        // 从左往右遍历 将相邻的相同的找出来,并计算好长度
        char[] chars = s.toCharArray();
        int count = 0;
        List<Integer> list = new ArrayList<>();
        for (int i = 0; i < chars.length; i++) {
           int num = 1;
           while ( i+1 <chars.length && chars[i+1] == chars[i] ){
               num++;
               i++;
           }
           list.add(num);
        }
        // 对列表里的数进行解析
        for (int i = 0; i<list.size();i++){
            int first = list.get(i);
            if(i+1>=list.size()){
                break;
            }
            int second = list.get(i+1);
            count += Integer.min(first,second);
        }
        return count;
    }

    public static void main(String[] args) {
        Problem696 p = new Problem696();
        System.out.println(p.countBinarySubstrings(""));
    }
}
